pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/AccessControlDefaultAdminRules.sol";
import "./INameManager.sol";
import "./IJNSAdminContract.sol";

contract JNSAdminContract is AccessControlDefaultAdminRules {
    bytes32 public constant CHANGE_RECIPIENT_ADMIN_ADDRESS_ROLE =
        keccak256("CHANGE_RECIPIENT_ADMIN_ADDRESS_ROLE");
    bytes32 public constant CHANGE_ORACLE_ADMIN_ADDRESS_ROLE =
        keccak256("CHANGE_ORACLE_ADMIN_ADDRESS_ROLE");
    bytes32 public constant NAME_MANAGER_ROLE = keccak256("NAME_MANAGER_ROLE");

    address payable public recipientAddress;
    address public oracleAddress;
    INameManager public nameManagerAddress;

    uint256[] private rentPrices;

    event RecipientAddressChanged(address indexed from, address indexed to);
    event OracleAddressChanged(address indexed from, address indexed to);
    event NameManagerAddressChanged(address indexed from, address indexed to);
    event EtherForwarded(address indexed from, address indexed to, uint amount);

    event RentPriceChanged(uint256[] prices);

    error RecipientAddressNotSet();
    error EtherNotForwarded();

    constructor(
        address _recipientAdminAddress,
        address _oracleAdminAddress,
        address _nameManagerAdminAddress,
        address payable _recipientAddress,
        uint256[] memory _rentPrices
    ) AccessControlDefaultAdminRules(3 days, msg.sender) {
        _checkNonZeroAddress(_recipientAdminAddress);
        _checkNonZeroAddress(_oracleAdminAddress);

        recipientAddress = _recipientAddress;
        rentPrices = _rentPrices;

        _grantRole(CHANGE_RECIPIENT_ADMIN_ADDRESS_ROLE, _recipientAdminAddress);
        _grantRole(CHANGE_ORACLE_ADMIN_ADDRESS_ROLE, _oracleAdminAddress);
        _grantRole(NAME_MANAGER_ROLE, _nameManagerAdminAddress);
    }

    function changeRecipientAddress(
        address payable _newRecipientAddress
    ) external onlyRole(CHANGE_RECIPIENT_ADMIN_ADDRESS_ROLE) {
        _checkNonZeroAddress(_newRecipientAddress);
        address oldAddress = recipientAddress;
        recipientAddress = _newRecipientAddress;
        emit RecipientAddressChanged(oldAddress, _newRecipientAddress);
    }

    function changeOracleAddress(
        address _newOracleAddress
    ) external onlyRole(CHANGE_ORACLE_ADMIN_ADDRESS_ROLE) {
        _checkNonZeroAddress(_newOracleAddress);
        address oldAddress = oracleAddress;
        oracleAddress = _newOracleAddress;
        emit OracleAddressChanged(oldAddress, _newOracleAddress);
    }

    function setNameManagerAddress(
        INameManager _newNameManagerAddress
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            address(_newNameManagerAddress) != address(0),
            "New NameManager cannot be the zero address"
        );
        INameManager oldAddress = nameManagerAddress;
        nameManagerAddress = _newNameManagerAddress;
        emit NameManagerAddressChanged(
            address(oldAddress),
            address(_newNameManagerAddress)
        );
    }

    receive() external payable {
        if (recipientAddress == address(0)) {
            revert RecipientAddressNotSet();
        }

        (bool success, ) = recipientAddress.call{value: msg.value}("");
        if (!success) {
            revert EtherNotForwarded();
        }

        emit EtherForwarded(msg.sender, recipientAddress, msg.value);
    }

    function _checkNonZeroAddress(address _address) private pure {
        require(_address != address(0), "Address cannot be the zero address");
    }

    function setRentPrices(
        uint256[] calldata prices
    ) external onlyRole(CHANGE_ORACLE_ADMIN_ADDRESS_ROLE) {
        require(prices.length == 5, "Prices array length must be of length 5");
        rentPrices = prices;
        emit RentPriceChanged(prices);
    }

    function getRentPrices() external view returns (uint256[] memory) {
        return rentPrices;
    }
}
