pragma solidity ^0.8.17;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./IJNSAdminContract.sol";
import "./INameManager.sol";

contract NameManager is INameManager {
    using EnumerableSet for EnumerableSet.Bytes32Set;

    bytes32 public constant NAME_MANAGER_ROLE = keccak256("NAME_MANAGER_ROLE");

    IJNSAdminContract public adminContract;

    mapping(uint32 => bool) public whitelistCharacters;
    EnumerableSet.Bytes32Set private takedownSet;
    EnumerableSet.Bytes32Set private restrictWordSet;

    constructor(address _adminContractAddress) {
        require(
            _adminContractAddress != address(0),
            "Admin contract address cannot be the zero address"
        );
        adminContract = IJNSAdminContract(_adminContractAddress);

        _whitelistCharactersAndNumbers();
    }

    modifier onlyNameManager() {
        require(
            adminContract.hasRole(NAME_MANAGER_ROLE, msg.sender),
            "Caller is not a name manager"
        );
        _;
    }

    function _whitelistCharactersAndNumbers() private {
        // Unicode values for 'a' to 'z' are 97 to 122
        for (uint32 i = 97; i <= 122; i++) {
            whitelistCharacters[i] = true;
        }
        // Unicode values for '0' to '9' are 48 to 57
        for (uint32 i = 48; i <= 57; i++) {
            whitelistCharacters[i] = true;
        }
    }

    function isWordWhitelisted(
        string calldata word
    ) public view returns (bool) {
        bytes memory wordBytes = bytes(word);
        for (uint32 i = 0; i < wordBytes.length; i++) {
            uint32 charCode = uint32(uint8(wordBytes[i]));
            if (!whitelistCharacters[charCode]) {
                return false;
            }
        }
        return true;
    }

    function isValid(string calldata name) public view returns (bool) {
        return isWordWhitelisted(name) && !isWordRestricted(name);
    }

    function addToTakedownList(
        string[] calldata tokenIds
    ) external onlyNameManager {
        for (uint i = 0; i < tokenIds.length; ++i) {
            bytes32 tokenIdHash = keccak256(abi.encodePacked(tokenIds[i]));
            takedownSet.add(tokenIdHash);
        }
    }

    function removeFromTakedownList(
        string[] calldata tokenIds
    ) external onlyNameManager {
        for (uint i = 0; i < tokenIds.length; ++i) {
            bytes32 tokenIdHash = keccak256(abi.encodePacked(tokenIds[i]));
            takedownSet.remove(tokenIdHash);
        }
    }

    function isInTakedownList(
        string memory tokenId
    ) public view returns (bool) {
        return takedownSet.contains(keccak256(abi.encodePacked(tokenId)));
    }

    function addToRestrictWords(
        string[] calldata words
    ) external onlyNameManager {
        for (uint i = 0; i < words.length; ++i) {
            bytes32 wordHash = keccak256(abi.encodePacked(words[i]));
            restrictWordSet.add(wordHash);
        }
    }

    function removeFromRestrictWords(
        string[] calldata words
    ) external onlyNameManager {
        for (uint i = 0; i < words.length; ++i) {
            bytes32 wordHash = keccak256(abi.encodePacked(words[i]));

            restrictWordSet.remove(wordHash);
        }
    }

    function isWordRestricted(string memory word) public view returns (bool) {
        return restrictWordSet.contains(keccak256(abi.encodePacked(word)));
    }

    function hashTakedownList() public view returns (bytes32[] memory) {
        return takedownSet.values();
    }

    function hashRestrictWords() public view returns (bytes32[] memory) {
        return restrictWordSet.values();
    }

    function _removeStringFromArray(
        string[] storage array,
        string memory value
    ) private {
        for (uint i = 0; i < array.length; i++) {
            if (keccak256(bytes(array[i])) == keccak256(bytes(value))) {
                array[i] = array[array.length - 1];
                array.pop();
                break;
            }
        }
    }
}
