pragma solidity ^0.8.17;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./IJNSAdminContract.sol";

/// @title Data Storage for JFIN Name Service Name Management
/// This contract handles storage and checks for whitelisted characters,
/// takedown hashes, and restricted words within a decentralized naming service.
contract NameManagerDataStore {
    using EnumerableSet for EnumerableSet.Bytes32Set;

    bytes32 public constant NAME_MANAGER_ROLE = keccak256("NAME_MANAGER_ROLE");

    mapping(uint32 => bool) private whitelistCharacters;
    EnumerableSet.Bytes32Set private takedownSet;
    EnumerableSet.Bytes32Set private restrictWordSet;

    IJNSAdminContract public adminContract;

    /// @dev Constructor that initializes the name manager data store.
    /// @param _adminContractAddress The address of the admin contract.
    constructor(address _adminContractAddress) {
        adminContract = IJNSAdminContract(_adminContractAddress);

        _whitelistCharactersAndNumbers();
    }

    /// @dev Ensures that the caller has the Name Manager role.
    modifier onlyNameManager() {
        require(
            adminContract.hasRole(NAME_MANAGER_ROLE, msg.sender),
            "Caller is not a name manager"
        );
        _;
    }

    /// @dev Adds a character to the whitelist.
    /// @param character The Unicode value of the character to whitelist.
    function addWhitelistCharacter(uint32 character) external onlyNameManager {
        whitelistCharacters[character] = true;
    }

    /// @dev Removes a character from the whitelist.
    /// @param character The Unicode value of the character to remove.
    function removeWhitelistCharacter(uint32 character) external onlyNameManager {
        whitelistCharacters[character] = false;
    }

    /// @dev Checks if a character is whitelisted.
    /// @param character The Unicode value of the character to check.
    /// @return True if the character is whitelisted.
    function isWhitelistCharacter(uint32 character) public view returns (bool) {
        return whitelistCharacters[character];
    }

    /// @dev Adds a hash to the takedown set.
    /// @param hash The hash to add to the takedown list.
    function addTakedown(bytes32 hash) external onlyNameManager {
        takedownSet.add(hash);
    }

    /// @dev Removes a hash from the takedown set.
    /// @param hash The hash to remove from the takedown list.
    function removeTakedown(bytes32 hash) external onlyNameManager {
        // if (!takedownSet.contains(hash))
        //     revert("takedown not found when remove");
        takedownSet.remove(hash);
    }

    /// @dev Adds a word hash to the restricted words set.
    /// @param hash The hash of the word to add to the restriction list.
    function addRestrictedWord(bytes32 hash) external onlyNameManager {
        restrictWordSet.add(hash);
    }

    /// @dev Removes a word hash from the restricted words set.
    /// @param hash The hash of the word to remove from the restriction list.
    function removeRestrictedWord(bytes32 hash) external onlyNameManager {
        // if (!restrictWordSet.contains(hash))
        //     revert("restricted word not found when remove");
        restrictWordSet.remove(hash);
    }

    /// @dev Checks if a word hash is in the restricted words set.
    /// @param hash The hash of the word to check.
    /// @return True if the hash is in the restricted set.
    function isRestrictedWord(bytes32 hash) public view returns (bool) {
        return restrictWordSet.contains(hash);
    }

    /// @dev Checks if a hash is in the takedown set.
    /// @param hash The hash to check.
    /// @return True if the hash is in the takedown list.
    function isTakedown(bytes32 hash) public view returns (bool) {
        return takedownSet.contains(hash);
    }

    /// @dev Retrieves all hashes from the takedown set.
    /// @return An array of all takedown hashes.
    function getHashedTakenDownList() public view returns (bytes32[] memory) {
        return takedownSet.values();
    }

    /// @dev Retrieves all word hashes from the restricted words set.
    /// @return An array of all restricted word hashes.
    function getHashedRestrictedWords() public view returns (bytes32[] memory) {
        return restrictWordSet.values();
    }

    /// @dev Pre-populates the whitelist with alphanumeric characters.
    /// Private function called during contract initialization.
    function _whitelistCharactersAndNumbers() private {
        // Unicode values for '0' to '9' are 48 to 57
        for (uint32 i = 48; i <= 57; i++) {
            whitelistCharacters[i] = true;
        }
        // Unicode values for 'a' to 'z' are 97 to 122
        for (uint32 i = 97; i <= 122; i++) {
            whitelistCharacters[i] = true;
        }
    }
}
